SELECT PRICE AS MAX_PRICE FROM PRODUCT ORDER BY PRICE DESC LIMIT 1;
SELECT LEFT(PRODUCT_CODE, 2) AS 'CATEGORY', COUNT(LEFT(PRODUCT_CODE, 2)) AS PRODUCTS FROM PRODUCT GROUP BY LEFT(PRODUCT_CODE, 2);
SELECT ORDER_ID, PRODUCT_ID, DATE_FORMAT(OUT_DATE, '%Y-%m-%d') AS 'OUT_DATE', IF(OUT_DATE IS NULL, '출고미정', IF(DATEDIFF('2022-05-01' , DATE_FORMAT(OUT_DATE, '%Y-%m-%d')) >= 0 , '출고완료', '출고대기')) AS '출고여부' FROM FOOD_ORDER ORDER BY ORDER_ID;